 INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = PTP.projectId
 LEFT JOIN M_Proposal_Master PPM ON  PPM.proposalId=PM.proposalId
 LEFT JOIN M_Client_Master CM ON CM.clientId = PPM.submittedByid
 WHERE PTP.deletedFlag=0 and PT.deletedFlag=0;
 
 ELSEIF (P_Action='B') THEN
 SELECT PTP.Id,PTP.preBidexcelDoc,PTP.preBidpdfDoc,PTP.Id 
 ,PM.projectId,PM.projectCode,date_format(PM.createdOn,'%d-%b-%Y')createdOn,PM.projectName,PM.projectAmount,PM.constructionArea,PM.length
 ,PT.tenderId,date_format(PT.openingDate,'%d-%b-%Y')openingDate,PT.emdAmount,date_format(PT.submissionDate,'%d-%b-%Y')submissionDate,PT.nitDocFileName
 ,PPM.letterNo,CM.clientName
  from T_PROJECT_TENDER_PREBID PTP 
 INNER JOIN T_PROJECT_TENDER PT ON PT.projectId = PTP.projectId
 INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = PTP.projectId
 LEFT JOIN M_Proposal_Master PPM ON  PPM.proposalId=PM.proposalId
 LEFT JOIN M_Client_Master CM ON CM.clientId = PPM.submittedByid
 WHERE PTP.Id=INT_ID and PTP.deletedFlag=0 and PT.deletedFlag=0;

 ELSEIF(P_Action='C') THEN 
  IF P_PMT REGEXP '^[0-9]+$' THEN  
		-- If P_PMT is purely numeric, search in T_PROJECT_MASTER
		SELECT projectId, projectCode 
		FROM T_PROJECT_MASTER 
		WHERE projectCode = P_PMT
		OR projectCode LIKE CONCAT('%', P_PMT, '%') and deletedFlag=0;

	ELSE  
		-- If P_PMT contains other formats, check if it is a tender number
		IF P_PMT LIKE 'TEN%' THEN  
				SELECT PM.projectId, PT.tenderNo
			FROM T_PROJECT_TENDER PT
			LEFT JOIN T_PROJECT_MASTER PM ON PT.projectId = PM.projectId
			WHERE PT.tenderNo LIKE CONCAT('%', P_PMT, '%') and PT.deletedFlag=0;
