CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_PROJECT_TENDER_TECHNICAL`(
IN P_Action CHAR(10),
IN INT_ID INT
)
BEGIN
   IF(P_Action='A') THEN # Get the basic Details
    
SELECT PM.projectId,PT.tenderId,date_format(PM.createdOn,'%d-%b-%Y')createdOn,
PM.projectName,PM.projectCode,PM.projectAmount,PM.projectAmount,PM.constructionArea,PM.length
    ,PTB.bidderName,PTB.bidderID
    
    from T_PROJECT_TENDER_BIDDER_DTLS PTB INNER JOIN 
     T_PROJECT_MASTER PM ON PM.projectId =PTB.projectId
      INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = PTB.tenderId
    where PTB.projectId=INT_ID  and PTB.deletedFlag=0;
    
    ELSEIF(P_Action='B') THEN # TechnicalBid View in List
    SELECT TT.ID, 
    CASE WHEN TT.technicalStatus='1' THEN 'Yes'
   WHEN TT.technicalStatus='2' THEN 'No' end as  technicalStatus
,date_format(TT.technicalDate,'%d-%b-%Y')technicalDate,date_format(TT.finanicalDate,'%d-%b-%Y')finanicalDate
,PT.tenderId,PM.projectId,PM.projectCode,PM.projectName,
date_format(PT.openingDate,'%d-%b-%Y')openingDate,date_format(PT.submissionDate,'%d-%b-%Y') submissionDate,
PPM.letterNo
from T_PROJECT_TENDER_TECHNICAL TT 
INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = TT.projectId
INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = TT.tenderId
LEFT JOIN M_Proposal_Master PPM ON  PPM.proposalId=PM.proposalId WHERE TT.deletedFlag=0;

ELSEIF(P_Action='C') THEN  # TechnicalBidder Details
SELECT 
    TT.ID,
