CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_Proposal`(
IN P_Action varchar(20),
IN P_SearchType VARCHAR(20),
IN P_proposalid INT,
IN P_Id INT,
IN P_FdrId INT,
IN P_Landid INT,
IN P_createdBy INT
)
BEGIN
	SET @ProposalWorkFlowID=1;
    
	IF P_Action = 'A' THEN
		SET @searchtype = P_SearchType;
		SET @createdby = P_createdBy;

		SELECT 
			pm.proposalid,
			(SELECT fdrId FROM T_Proposal_FDR_Details WHERE proposalId = pm.proposalid) AS fdrId,
			pm.proposerName AS proposalName,
			pm.categoryid,
			pm.subCategoryid,
			pm.letterNo,
			pm.proposalLetterDate,
			pm.letterDocPath,
			pm.subject,
			pm.proposalStatus,
			pm.statusId,
			ld.levelName AS district,
			cm.categoryName,
			scm.subCategoryName,
            (
				CASE 
				WHEN (pm.proposalStatus = 1 AND pm.statusId = 0) THEN 'Draft'
				WHEN (pm.proposalStatus = 1 AND pm.statusId = 1) THEN 
				CONCAT(
					'Pending at ',
					(SELECT M_ADM_DESIGNATION.NVCHALIASNAME 
					 FROM Approval_Config 
					 JOIN M_ADM_DESIGNATION 
					   ON Approval_Config.intdesigid = M_ADM_DESIGNATION.INTDESIGID 
					 WHERE FormName = @ProposalWorkFlowID 
					   AND intlevel = (pmat.stepId)),
					' from ', DATE_FORMAT(pmat.createdOn, '%d-%b-%Y'),
					' (', DATEDIFF(NOW(), pmat.createdOn), ' days ago)'
				)
				WHEN (pm.proposalStatus = 1 AND pm.statusId = 2) THEN 
				CONCAT(
					'Reverted by ',
					(SELECT NVCHALIASNAME 
					 FROM M_ADM_DESIGNATION 
					 WHERE INTDESIGID = (
						 SELECT INTDESIGNATIONID FROM M_POR_USER WHERE INTUSERID = pmat.createdBy
					 )),
					' from ', DATE_FORMAT(pmat.createdOn, '%d-%b-%Y'),
					' (', DATEDIFF(NOW(), pmat.createdOn), ' days ago)'
				)

				WHEN ((pm.proposalStatus = 1 AND pm.statusId = 3) OR (pm.proposalStatus = 2 AND pm.statusId = 1)) THEN 
				CONCAT(
					'Pending at ',
					(SELECT M_ADM_DESIGNATION.NVCHALIASNAME 
					 FROM Approval_Config 
					 JOIN M_ADM_DESIGNATION 
					   ON Approval_Config.intdesigid = M_ADM_DESIGNATION.INTDESIGID 
					 WHERE FormName = @ProposalWorkFlowID 
					   AND intlevel = pmat.stepId),
					' from ', DATE_FORMAT(pmat.createdOn, '%d-%b-%Y'),
					' (', DATEDIFF(NOW(), pmat.createdOn), ' days ago)'
				)
				WHEN (pm.proposalStatus = 1 AND pm.statusId = 4) THEN 
				CONCAT(
					'Rejected by ',
					(SELECT NVCHALIASNAME 
					 FROM M_ADM_DESIGNATION 
					 WHERE INTDESIGID = (
						 SELECT INTDESIGNATIONID FROM M_POR_USER WHERE INTUSERID = pmat.createdBy
					 )),
					' from ', DATE_FORMAT(pmat.createdOn, '%d-%b-%Y'),
					' (', DATEDIFF(NOW(), pmat.createdOn), ' days ago)'
				)
				ELSE 'None'
			END

			) AS status,
			pm.deletedFlag,
			(
				CASE WHEN pm.createdBy = @createdby AND @searchtype = 'NewApplication' THEN 1 
                WHEN pm.createdBy != @createdby AND @searchtype = 'NewApplication' THEN 2
                ELSE 3 END) AS 'type',
        pm.stepId as stepId
		FROM M_Proposal_Master pm
		JOIN M_Adm_LevelDetails ld ON pm.districtid = ld.levelDetailId
		JOIN M_Project_Category_Master cm ON pm.categoryid = cm.categoryid
		JOIN M_Project_Subcategory_Master scm ON pm.subCategoryid = scm.subCategoryid
        LEFT JOIN T_Proposal_Action_Taken pmat ON pm.proposalid=pmat.proposalId AND pmat.ActionId=(select MAX(ActionId) from T_Proposal_Action_Taken)
		WHERE 
			pm.deletedFlag = 0
            AND
			(
				(@searchtype = 'NewApplication' AND (
					-- Enforce strict condition first
					(
						pm.proposalStatus = 1 
						AND pm.statusId IN (0,2) 
						AND @createdBy = pm.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						pm.proposalStatus = 1 
						-- AND pm.statusId IN (0,3,4)
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId and pm.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 2 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId
								  AND conf.FormName = @ProposalWorkFlowID 
								  AND conf.intDeleteflag = 0 
								  AND conf.ApprovalType = 2
								  AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
									)
								  )
							))
						)
					)
				)
            )
			OR 
			(@searchtype = 'InProgress' AND (
					-- Enforce strict condition first
					(
						pm.proposalStatus = 1 
						AND pm.statusId IN (1) 
						AND @createdBy = pm.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						pm.proposalStatus = 1 
						-- AND pm.statusId IN (0,3,4)
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId and pm.stepId = 3
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 3 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId-1
								  AND conf.FormName = @ProposalWorkFlowID 
								  AND conf.intDeleteflag = 0 
								  AND conf.ApprovalType = 2
								  AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
									)
								  )
							))
						)
					)
				)
            )
            
            OR
            (@searchtype = 'Approved' AND (
					-- Enforce strict condition first
					(
						pm.proposalStatus = 1 
						AND pm.statusId IN (3) 
						AND @createdBy = pm.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						pm.proposalStatus = 1 
						-- AND pm.statusId IN (0,3,4)
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId and pm.stepId = 4
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 4 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId
								  AND conf.FormName = @ProposalWorkFlowID 
								  AND conf.intDeleteflag = 0 
								  AND conf.ApprovalType = 2
								  AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
									)
								  )
							))
						)
					)
				)
            )
            OR
            (@searchtype = 'Cancelled' AND 
					(
						pm.proposalStatus = 1 
						AND pm.statusId IN (4)
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId and pm.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 2 AND @createdBy IN 
                            (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId
								AND conf.FormName = @ProposalWorkFlowID 
								AND conf.intDeleteflag = 0 
								AND conf.ApprovalType = 2
								AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
											AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
										)
									)
							)
						)
					)
				)
            )
		)
			
		ORDER BY pmat.createdOn DESC;

		SET @NewApplication=
        (SELECT count(*) from M_Proposal_Master pm
			where pm.deletedFlag=0 AND
			-- Enforce strict condition first
			(
				pm.proposalStatus = 1 
				AND pm.statusId IN (0,2) 
				AND @createdBy = pm.createdBy
			)
			-- If strict condition does not apply, execute other conditions
			OR 
			(
				pm.proposalStatus = 1 
				-- AND pm.statusId IN (0,3,4)
				AND (
					-- If @createdBy is the officer, stop execution here
					@createdBy = pm.officerId and pm.stepId = 2
					-- Otherwise, check Approval_Config condition
					OR (@createdBy <> pm.officerId AND pm.stepId > 2 AND @createdBy IN (
						SELECT usr.INTUSERID 
						FROM Approval_Config conf
						JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
						WHERE conf.intlevel = pm.stepId
						  AND conf.FormName = @ProposalWorkFlowID 
						  AND conf.intDeleteflag = 0 
						  AND conf.ApprovalType = 2
						  AND (
							conf.intlevel <> 3 
							OR (conf.intlevel = 3 
								AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
							)
						  )
					))
				)
			)
		);
        
		SET @InProgress=
        (SELECT count(*) from M_Proposal_Master pm
			where pm.deletedFlag=0 AND
			-- Enforce strict condition first
			(
				pm.proposalStatus = 1 
				AND pm.statusId IN (1) 
				AND @createdBy = pm.createdBy
			)
			-- If strict condition does not apply, execute other conditions
			OR 
			(
				pm.proposalStatus = 1 
				-- AND pm.statusId IN (0,3,4)
				AND (
					-- If @createdBy is the officer, stop execution here
					@createdBy = pm.officerId and pm.stepId = 3
					-- Otherwise, check Approval_Config condition
					OR (@createdBy <> pm.officerId AND pm.stepId > 3 AND @createdBy IN (
						SELECT usr.INTUSERID 
						FROM Approval_Config conf
						JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
						WHERE conf.intlevel = pm.stepId-1
						  AND conf.FormName = @ProposalWorkFlowID 
						  AND conf.intDeleteflag = 0 
						  AND conf.ApprovalType = 2
						  AND (
							conf.intlevel <> 3 
							OR (conf.intlevel = 3 
								AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
							)
						  )
					))
				)
			)
		);
        SET @Approved=
        (SELECT count(*) from M_Proposal_Master pm
			where pm.deletedFlag=0 AND
			-- Enforce strict condition first
			(
				pm.proposalStatus = 1 
				AND pm.statusId IN (3) 
				AND @createdBy = pm.createdBy
			)
			-- If strict condition does not apply, execute other conditions
			OR 
			(
				pm.proposalStatus = 1 
				-- AND pm.statusId IN (0,3,4)
				AND (
					-- If @createdBy is the officer, stop execution here
					@createdBy = pm.officerId and pm.stepId = 4
					-- Otherwise, check Approval_Config condition
					OR (@createdBy <> pm.officerId AND pm.stepId > 4 AND @createdBy IN (
						SELECT usr.INTUSERID 
						FROM Approval_Config conf
						JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
						WHERE conf.intlevel = pm.stepId
						  AND conf.FormName = @ProposalWorkFlowID 
						  AND conf.intDeleteflag = 0 
						  AND conf.ApprovalType = 2
						  AND (
							conf.intlevel <> 3 
							OR (conf.intlevel = 3 
								AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
							)
						  )
					))
				)
			)
		);
        SET @Cancelled=
        (SELECT count(*) from M_Proposal_Master pm
			where pm.deletedFlag=0 AND
			(
						pm.proposalStatus = 1 
						AND pm.statusId IN (4)
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId and pm.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 2 AND @createdBy IN 
                            (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId
								AND conf.FormName = @ProposalWorkFlowID 
								AND conf.intDeleteflag = 0 
								AND conf.ApprovalType = 2
								AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
											AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
										)
									)
							)
						)
					)
				)
		);
        Select @NewApplication as NewApplication,@InProgress as InProgress,@Approved as Approved,@Cancelled as Cancelled,
        (@NewApplication+@InProgress+@Approved+@Cancelled) as TotalApplication;
	ELSEIF P_Action = 'B' THEN
        select 2;
	ELSEIF P_Action = 'C' THEN
		SELECT pm.`proposalid`,
			pm.`FyId`,
			pm.`submittedByid`,
            cm.`clientName` as submittedBy,
			pm.`proposerName` as proposalName,
			pm.`categoryid`,
			c.categoryName,
			pm.`subCategoryid`,
			sc.subCategoryName,
			pm.`projectTypeid`,
			pm.`districtid`,
			d.`levelName` as district,
			pm.`officerId`,
			pm.`ulbId`,
            (select levelName from M_Adm_LevelDetails where levelDetailId=pm.`ulbId`) as ulb,
            (select VCHUSERNAME from CPKG_1_USER where INTUSERID=pm.`officerId`) as officername,
			pm.`letterNo`,
			pm.`proposalLetterDate`,
			pm.`letterDocPath`,
			pm.`subject`,
			pm.`proposalStatus`,
			pm.`remarks`,
            (
				CASE 
                WHEN (pm.proposalStatus = 1 AND pm.statusId = 0) THEN 'Draft'
                WHEN (pm.proposalStatus = 1 AND pm.statusId = 1) THEN 'Pending'
                WHEN (pm.proposalStatus = 1 AND pm.statusId = 2) THEN 'Reverted'
                WHEN (pm.proposalStatus > 1) THEN 'Approved'
                WHEN (pm.proposalStatus = 1 AND pm.statusId = 4) THEN 'Cancelled'
                ELSE 'None'
                END
			) as status,
			pm.statusId,
            pm.stepId
		FROM M_Proposal_Master pm 
		join M_Adm_LevelDetails d on pm.districtid=d.levelDetailId and d.parentid=1 and d.levelid=2 and d.deletedflag=0
		join M_Project_Category_Master c on pm.categoryid=c.categoryid
