CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_QUERYMANAGEMENT`(
    IN P_Action CHAR(30),
    IN P_createdBy INT,
    IN INT_ID INT
)
BEGIN
    IF (P_Action = 'A') THEN -- View Query View in list
        SELECT 
            description, 
            queryId, 
            CASE
                WHEN queryTypeId = 1 THEN 'Incident'
                WHEN queryTypeId = 2 THEN 'Query'
                ELSE 'Other'
            END AS queryType, 
            projectId,
            queryDocPath,  
            photoDocPath,
            createdBy,
            CASE
                WHEN (
                    SELECT statusId 
                    FROM T_VENDOR_QUERY_RESPONSE 
                    WHERE queryId = T_VENDOR_QUERY_ADD.queryId 
                    ORDER BY actionDate DESC 
                    LIMIT 1
                ) IS NULL THEN 'Pending'
                WHEN (
                    SELECT statusId 
                    FROM T_VENDOR_QUERY_RESPONSE 
                    WHERE queryId = T_VENDOR_QUERY_ADD.queryId 
                    ORDER BY actionDate DESC 
                    LIMIT 1
                ) = 1 THEN 'In Progress'
                WHEN (
                    SELECT statusId 
                    FROM T_VENDOR_QUERY_RESPONSE 
                    WHERE queryId = T_VENDOR_QUERY_ADD.queryId 
                    ORDER BY actionDate DESC 
                    LIMIT 1
                ) = 2 THEN 'Closed'
                ELSE 'Unknown'
            END AS status
        FROM T_VENDOR_QUERY_ADD;
       -- WHERE createdBy = P_createdBy
      --  AND deletedFlag = 0;

    ELSEIF (P_Action = 'B') THEN -- Get details for Edit
        SELECT 
            queryId, 
            queryTypeId,
            projectId,
            queryDocPath,
            photoDocPath,
            description 
        FROM T_VENDOR_QUERY_ADD 
        WHERE queryId = INT_ID;

    END IF;
END ;;
