CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_Search`(
IN P_Action varchar(20),
IN P_FyId INT,
IN P_letterNo VARCHAR(50),
IN P_submittedByid INT,
IN P_categoryid INT,
IN P_subCategoryid INT,
IN P_projectTypeid INT,
IN P_districtid INT,
IN P_ulbId INT
)
BEGIN
	IF P_Action = 'A' THEN -- Ranjan for proposal search
		SELECT 
			pm.proposalid,
			pm.proposalName,
			pm.categoryid,
			pm.subCategoryid,
			pm.letterNo,
			pm.proposalLetterDate,
			pm.letterDocPath,
			pm.subject,
			pm.proposalStatus,
			pm.statusId,
			ld.levelName AS district,
			cm.categoryName,
			scm.subCategoryName,
			'Draft' AS status,
            (
				CASE WHEN (proposalStatus = 1 AND statusId = 0) THEN 'Draft'
                WHEN (proposalStatus = 2 AND statusId = 4) THEN 'Reverted by TS'
                WHEN (proposalStatus = 1 AND statusId = 1) THEN 'Pending at TS'
                WHEN (proposalStatus = 2 AND statusId = 2) THEN 'Approved by TS'
                WHEN (pm.deletedFlag) THEN 'Cancelled'
                ELSE 'None'
                END
			) as status,
			pm.deletedFlag
			FROM M_Proposal_Master pm
			JOIN M_Adm_LevelDetails ld ON pm.districtid = ld.levelDetailId
			JOIN M_Project_Category_Master cm ON pm.categoryid = cm.categoryid
			JOIN M_Project_Subcategory_Master scm ON pm.subCategoryid = scm.subCategoryid
			WHERE ld.parentid = 1 
			AND ld.levelid = 2
            AND (P_FyId = 0 OR pm.FyId = P_FyId)
			AND (P_categoryid = 0 OR pm.categoryid = P_categoryid) 
			AND (P_letterNo IS NULL OR pm.letterNo = P_letterNo)
            AND (P_submittedByid = 0 OR pm.submittedByid = P_submittedByid) 
            AND (P_subCategoryid = 0 OR pm.subCategoryid = P_subCategoryid) 
            AND (P_projectTypeid = 0 OR pm.projectTypeid = P_projectTypeid) 
            AND (P_districtid = 0 OR pm.districtid = P_districtid) 
            AND (P_ulbId = 0 OR pm.ulbId = P_ulbId) 
			ORDER BY pm.proposalid DESC;
			SELECT 
				SUM(CASE 
					WHEN deletedFlag = 0 AND ((proposalStatus = 1 AND statusId = 0) 
						OR (proposalStatus = 2 AND statusId = 4)) 
					THEN 1 ELSE 0 END) AS NewApplication,
				SUM(CASE 
					WHEN deletedFlag = 0 AND (proposalStatus = 1 AND statusId = 1) 
					THEN 1 ELSE 0 END) AS InProgress,
				SUM(CASE 
					WHEN deletedFlag = 0 AND (proposalStatus = 2 AND statusId = 2) 
					THEN 1 ELSE 0 END) AS Approved,
				SUM(CASE 
					WHEN deletedFlag = 1 THEN 1 ELSE 0 END) AS Cancelled,
				SUM(CASE 
				WHEN deletedFlag = 0 AND ((proposalStatus = 1 AND statusId = 0) 
					OR (proposalStatus = 2 AND statusId = 4)) 
				THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN deletedFlag = 0 AND (proposalStatus = 1 AND statusId = 1) 
					THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN deletedFlag = 0 AND (proposalStatus = 2 AND statusId = 2) 
					THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN deletedFlag = 1 THEN 1 ELSE 0 END) 
			AS TotalApplication
		FROM M_Proposal_Master
        WHERE
			(P_FyId = 0 OR M_Proposal_Master.FyId = P_FyId)
			AND (P_categoryid = 0 OR M_Proposal_Master.categoryid = P_categoryid) 
			AND (P_letterNo IS NULL OR M_Proposal_Master.letterNo = P_letterNo)
            AND (P_submittedByid = 0 OR M_Proposal_Master.submittedByid = P_submittedByid) 
            AND (P_subCategoryid = 0 OR M_Proposal_Master.subCategoryid = P_subCategoryid) 
            AND (P_projectTypeid = 0 OR M_Proposal_Master.projectTypeid = P_projectTypeid) 
            AND (P_districtid = 0 OR M_Proposal_Master.districtid = P_districtid)
            AND (P_ulbId = 0 OR M_Proposal_Master.ulbId = P_ulbId);
        ELSEIF P_Action = 'B' THEN -- Ranjan for TS search
		SELECT
			ts.tsId,
			pm.proposalId,
			(select levelName from M_Adm_LevelDetails where levelDetailId=pm.districtid and parentid=1 and levelid=2) as district,
			sm.SchemeName as scheme,
			cl.clientName as submittedBy,
			cm.categoryName,
			scm.subCategoryName,
			pm.letterNo as proposalLetterNo,
			pm.proposalLetterDate,
			pm.subject as proposalSubject,
			pm.letterDocPath as proposalletterPath,
			ta.estimattedCost as taestimatedCost,
			aa.aaLetterPath,
			aa.aaLetterDate,
			ts.tsLetterPath,
			ts.tsLetterGendate,
			-- ts.tsLetterDate
			(
				CASE 
					WHEN (pm.proposalStatus = 3 AND pm.statusId = 2 and ts.generateStatus is null) THEN 'Pending Generate'
					WHEN (pm.proposalStatus = 3 AND pm.statusId = 2 and ts.generateStatus=1) THEN 'Pending Upload'
					WHEN (pm.proposalStatus = 3 AND pm.statusId = 2 and ts.generateStatus=2) THEN 'Pending Submission'
					ELSE 'None'
				END
			) as status
			FROM M_Proposal_Master pm 
			left join T_PROPOSAL_TA_DETAILS ta on ta.proposalid=pm.proposalid
			join T_PROPOSAL_AA_DETAILS aa on aa.proposalid=pm.proposalid
			
			left join T_PROPOSAL_TS_DETAILS ts on ts.proposalId=pm.proposalid
			join M_Scheme_Master sm on aa.schemeId=sm.schemeId
			JOIN M_Project_Category_Master cm ON pm.categoryid = cm.categoryid
			JOIN M_Project_Subcategory_Master scm ON pm.subCategoryid = scm.subCategoryid
			join M_Client_Master cl on pm.submittedByid=clientId
			AND (
				-- Apply filter dynamically based on P_SearchType
					CASE 
						WHEN 'NewApplication' = P_SearchType THEN (pm.proposalStatus = 3 AND pm.statusId = 2 and aa.deletedFlag=0)
						WHEN 'Inprogress' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId = 2 and aa.deletedFlag=0)
						WHEN 'Approved' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId = 2 and aa.deletedFlag=0)
						WHEN 'Cancelled' = P_SearchType THEN (aa.deletedFlag = 1)
						ELSE 1 = 1 -- No additional filter applied if P_SearchType is not specified
					END
				)
			WHERE
            (P_FyId = 0 OR pm.FyId = P_FyId)
			AND (P_categoryid = 0 OR pm.categoryid = P_categoryid) 
			AND (P_letterNo IS NULL OR pm.letterNo = P_letterNo)
            AND (P_submittedByid = 0 OR pm.submittedByid = P_submittedByid) 
            AND (P_subCategoryid = 0 OR pm.subCategoryid = P_subCategoryid) 
            AND (P_projectTypeid = 0 OR pm.projectTypeid = P_projectTypeid) 
            AND (P_districtid = 0 OR pm.districtid = P_districtid) 
            AND (P_ulbId = 0 OR pm.ulbId = P_ulbId) 
			ORDER BY aa.createdOn DESC;
			SELECT 
				SUM(CASE 
					WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 3 AND pm.statusId = 2)
					THEN 1 ELSE 0 END) AS NewApplication,
				SUM(CASE 
					WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 3 AND pm.statusId = 2) 
					THEN 1 ELSE 0 END) AS InProgress,
				SUM(CASE 
					WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 4 AND pm.statusId = 2) 
					THEN 1 ELSE 0 END) AS Approved,
				SUM(CASE 
					WHEN aa.deletedFlag = 1 THEN 1 ELSE 0 END) AS Cancelled,
				SUM(CASE 
				WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 3 AND pm.statusId = 2) 
				THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 3 AND pm.statusId = 2) 
					THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN aa.deletedFlag = 0 AND (pm.proposalStatus = 4 AND pm.statusId = 2) 
					THEN 1 ELSE 0 END) 
				+
				SUM(CASE 
					WHEN aa.deletedFlag = 1 THEN 1 ELSE 0 END) 
				AS TotalApplication
			FROM M_Proposal_Master pm 
			join T_PROPOSAL_AA_DETAILS aa on aa.proposalid=pm.proposalid
            WHERE
			(P_FyId = 0 OR M_Proposal_Master.FyId = P_FyId)
			AND (P_categoryid = 0 OR pm.categoryid = P_categoryid) 
			AND (P_letterNo IS NULL OR pm.letterNo = P_letterNo)
            AND (P_submittedByid = 0 OR pm.submittedByid = P_submittedByid) 
            AND (P_subCategoryid = 0 OR pm.subCategoryid = P_subCategoryid) 
            AND (P_projectTypeid = 0 OR pm.projectTypeid = P_projectTypeid) 
            AND (P_districtid = 0 OR pm.districtid = P_districtid)
            AND (P_ulbId = 0 OR pm.ulbId = P_ulbId);
            IF P_Action = 'B' then
            select 1;
            END IF;
	END IF;
    
END ;;
