)
BEGIN
SET @P_Proposal=P_ProposalId;
	IF P_Action = 'A' THEN # FOR PROPOSAL
		SELECT M.proposalid ProposalId, (SELECT levelname FROM M_Adm_LevelDetails
			WHERE levelDetailId= districtid) District,
			M.subject ProposalSubject,
			(SELECT clientName FROM M_Client_Master where clientid=submittedByid and deletedflag=0)
