            stepId
		FROM BUIDCO_devdb.M_Proposal_Master M 
		WHERE proposalStatus>=1 AND M.deletedFlag=0 AND (M.PROPOSALID=@P_Proposal OR @P_Proposal=0);
		# FOR DPR
		Select consultantName as PreparedBy, DATE_FORMAT(SubmittedOn,'%d-%b-%Y')SubmittedOn, AgencyName, DATE_FORMAT(LOADate,'%d-%b-%Y') LOADate, LOANo, LOALetterPath, PPTPath, FDRPath, MapPath,
		DesignLayout ,duration AS workduration
		from T_Proposal_FDR_Details join M_Consultant on T_Proposal_FDR_Details.PreparedBy=M_Consultant.consultant_id where ProposalID = @P_Proposal and T_Proposal_FDR_Details.DeletedFlag = 0;
		# FOR PROJECT SITE
		Select l.Area,l.Nocpath,l.Plannedmap,l.Address,l.Lattitude,l.Longitude,l.Khatano,l.Khesrano,group_concat(I.Docpath separator ', ') as docpath
		From T_Proposal_Land_Information l inner join T_Proposal_Land_Document I on l.Landid = I.Landid where l.Deletedflag = 0 and I.Deletedflag = 0
		And l.proposalid = @p_proposal and l.deletedflag = 0 and  I.deletedflag = 0
		Group by l.Area, l.Nocpath, l.Plannedmap, l.Address, l.Lattitude, l.Longitude, l.Khatano, l.Khesrano;

		# FOR TA DETAILS
		Select taletterpath,taletterno,date_format(taLetterDate,'%d-%b-%y')taletterdate,estimattedcost,
		'Admin'Taapprovedby,nazarimap, drawingpath,
		Otherdocpath,taremarks,
        (select 
			  VCHFULLNAME 
			from 
			  M_POR_USER 
			where 
			  INTUSERID = createdby)createdby

		,date_format(createdon,'%d-%b-%y')createdon
		,(select stepId from M_Proposal_Master where proposalid=@p_proposal)stepId
        ,generateStatus
		FROM T_PROPOSAL_TA_DETAILS
		where deletedflag=0 and proposalId=@p_proposal;

		# FOR AA DETAILS
		Select 
			aaLetterPath AALetterPath, 
			aaLetterNo AALetterNo, 
			date_format(aaLetterDate, '%d-%b-%y') AALetterDate, 
		  (
			Select 
			  SchemeName Value 
			from 
			  M_Scheme_Master 
			where 
			  deletedFlag = 0 
			  and schemeId = T_PROPOSAL_AA_DETAILS.schemeId
		  ) AASchemeName, 
		otherDocPath AAOtherDocuemntPath, 
		isMultipleProject IsMultipleProject, 
		'Admin' AAApprovedBy, 
		  aaremarks AARemarks, 
		  (
			select 
			  VCHFULLNAME 
			from 
			  M_POR_USER 
			where 
			  INTUSERID = createdby
		  ) AACreatedBy, 
		  CAST(
			(
			  SELECT 
				GROUP_CONCAT(
				  CONCAT(
					PTC.aaPartid, '|', PTC.projectPart, 
					'|', PTC.partDesc, '|', PTC.partAmount
				  ) SEPARATOR '^'
				) 
			  FROM 
				T_Proposal_AA_Part_Details PTC 
			  WHERE 
				PTC.aaId = T_PROPOSAL_AA_DETAILS.aaId
				AND PTC.deletedFlag = 0
			) AS CHAR
		  ) AS ProjectDetailsList, 
		  date_format(createdon, '%d-%b-%y') createdon
		FROM 
		  T_PROPOSAL_AA_DETAILS
		where 
		deletedflag = 0 
		and proposalId = @p_proposal;
		# FOR TS DETAILS
SELECT ts.tsId,
			ts.`proposalId`,
			ts.`tsLetterPath`,
			ts.`tsLetterGendate`,
			ts.`tsLetterNo`,
			ts.`tsLetterDate`,
			ts.`tsApprovedBy`,
			ts.`executionMode`,
			ts.`boqPath`,
			ts.`skilledLabour`,
			ts.`unskilledLabour`,
			ts.`otherDocPath`,
			ts.`tsRemarks`,
			ta.estimattedCost as taEstimatedCost,
			aa.aaEstimatedAmount as aaEstimatedAmount,
            ta.taLetterPath,
            aa.aaLetterPath
			FROM T_PROPOSAL_TS_DETAILS ts
			left join T_PROPOSAL_TA_DETAILS ta on ta.proposalId=ts.proposalId
			left join T_PROPOSAL_AA_DETAILS aa on aa.proposalId=ts.proposalId
			where ts.proposalId=@p_proposal;

END IF;   
	

 
END ;;
