CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_TA_Approval`(
IN P_Action CHAR(30),
IN P_ProposalId int,
IN P_TAID INT,
IN P_FyId INT,
IN P_letterNo VARCHAR(50),
IN P_submittedByid INT,
IN P_categoryid INT,
IN P_subCategoryid INT,
IN P_projectTypeid INT,
IN P_districtid INT,
IN P_ulbId INT,

in P_CreatedBy int

)
BEGIN
DECLARE TAWorkFlowID INT DEFAULT 3;
SET @P_Proposal=P_ProposalId;
SET @searchtype = 'NewApplication';
SET @createdBy=P_CreatedBy;
	IF P_Action = 'A' THEN
		SELECT M.proposalid ProposalId, 
        (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId= M.districtid) District,
		M.`subject` ProposalSubject,
        COALESCE(D.TAID,0) TAId,
		(SELECT clientName FROM M_Client_Master where clientid=M.submittedByid) ProposalSubmittedBy,
		DATE_FORMAT(M.createdon, '%d-%b-%Y')createdon,
		(select categoryName from M_Project_Category_Master where categoryId=M.categoryid) Proposalcategory,
		(select subCategoryName from M_Project_Subcategory_Master where subcategoryId=M.subCategoryid) Proposalsubcategory,
		COALESCE(generateStatus,0) generateStatus,
		M.stepId stepId,
		date_format(D.taLetterDate,'%d-%b-%Y') TADate,
		taLetterPath as TAletterPath,
        DATE_FORMAT(letterUploadDate, '%d-%b-%Y') TAUploadedDate,
		letterno ProposalLetterNo,
        DATE_FORMAT(proposalLetterDate, '%d-%b-%Y') LetterDate,
        letterdocpath ProposalLetter,
        otherDocPath TAOtherDocPath,
        (Case when @searchtype = 'NewApplication' then 2 else 3 end) as type
		FROM M_Proposal_Master M LEFT JOIN T_PROPOSAL_TA_DETAILS D ON M.PROPOSALID=D.PROPOSALID
		AND D.deletedFlag=0
        WHERE 
			M.deletedFlag = 0
			AND (
					-- Enforce strict condition first
					(
						M.proposalStatus = 2 
						AND M.statusId IN (2,1) 
						AND @createdBy = M.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						M.proposalStatus = 2 
						AND M.statusId NOT IN (2,1) -- Ensures this runs only if (0,2) condition is false
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = M.officerId AND M.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> M.officerId AND M.stepId > 2 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = M.stepId
								AND conf.FormName = TAWorkFlowID 
								AND conf.intDeleteflag = 0 
								AND conf.ApprovalType = 2
								AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = M.districtid)
										)
									)
							))
            )
        )
    )
		AND (M.PROPOSALID=@P_Proposal OR @P_Proposal=0)
		AND   (P_FyId = 0 OR M.FyId = P_FyId)
		AND (P_categoryid = 0 OR M.categoryid = P_categoryid) 
		AND (P_letterNo IS NULL OR M.letterNo = P_letterNo)
		AND (P_submittedByid = 0 OR M.submittedByid = P_submittedByid) 
		AND (P_subCategoryid = 0 OR M.subCategoryid = P_subCategoryid) 
		AND (P_projectTypeid = 0 OR M.projectTypeid = P_projectTypeid) 
		AND (P_districtid = 0 OR M.districtid = P_districtid) 
		AND (P_ulbId = 0 OR M.ulbId = P_ulbId);
	END IF;   
	IF P_Action = 'B' THEN
		SELECT D.TAID, M.proposalid ProposalId, (SELECT levelname FROM M_Adm_LevelDetails WHERE levelDetailId= districtid) District,
		M.subject ProposalSubject,
		(SELECT clientName FROM M_Client_Master where clientid=submittedByid and deletedflag=0)
		ProposalSubmittedBy,
		DATE_FORMAT(M.createdon, '%d-%b-%Y')createdon,
		(select categoryName from M_Project_Category_Master where categoryId=M.categoryid and deletedflag=0) Proposalcategory,
		(select subCategoryName from M_Project_Subcategory_Master where subcategoryId=M.subCategoryid and deletedflag=0) 
		Proposalsubcategory,
        (Select IFNULL(SUM(area),0) from T_Proposal_Land_Information where proposalId=M.proposalid) as ProposalArea,
		(select VCHUSERNAME from M_POR_USER where INTUSERID=M.CREATEDBY) CreatedBy,
		COALESCE(D.generateStatus,0) generateStatus,date_format(D.taLetterDate,'%d-%b-%Y')TADate,
		taLetterPath as TAletterPath,DATE_FORMAT(letterUploadDate, '%d-%b-%Y') TAUploadedDate,
		letterno ProposalLetterNo,DATE_FORMAT(proposalLetterDate, '%d-%b-%Y') LetterDate,letterdocpath ProposalLetter,
		nazariMap TANazariMap,drawingPath TADrawingPath,otherDocPath TAOtherDocPath,taRemarks TAremarks,D.taLetterNo TAletterNo,
		estimattedCost TAestimattedcost
		FROM BUIDCO_devdb.M_Proposal_Master M 	LEFT JOIN T_PROPOSAL_TA_DETAILS D ON M.PROPOSALID=D.PROPOSALID
        WHERE M.PROPOSALID=P_ProposalId
		AND M.deletedFlag=0 ;

		-- WHERE  (M.proposalStatus, M.STATUSID) IN ((1, 1), (2, 2)) AND M.deletedFlag=0 AND
		# proposalStatus=1 AND M.deletedFlag=0 AND M.STATUSID=1 AND 
		-- (D.TAID=@P_TAID OR @P_TAID=0);

	END IF;     
END ;;
