CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_TENDER_BIDDER_DTLS`(
IN P_Action CHAR(10),
IN INT_ID INT
)
BEGIN
 IF(p_Action='A') THEN
SELECT 
    PT.tenderId, 
    COUNT(PTB.tenderId) AS totalBider, 
    PM.projectId, 
    PM.projectCode, 
    PM.createdOn, 
    PM.projectName, 
    PM.projectAmount, 
