INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = PTB.tenderId
INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = PTB.projectId
LEFT JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId
LEFT JOIN M_Client_Master CM ON CM.clientId = PPM.submittedByid
WHERE PTB.deletedFlag = 0
GROUP BY 
    PT.tenderId, 
    PM.projectId, PM.projectCode, PM.createdOn, PM.projectName, 
    PM.projectAmount, PM.constructionArea, PM.length,
    PT.openingDate, PT.emdAmount, PT.submissionDate, PT.nitDocFileName,
    PPM.letterNo, CM.clientName
ORDER BY PT.tenderId ASC;
 
  ELSEIF(p_Action='B') THEN 
 SELECT 
    
 PTB.bidderID, PTB.bidderName,PTB.emailId,PTB.emdNumber,PTB.amount,PTB.contactNo,PTB.bankName,date_format(PTB.emdIssueDate,'%d-%b-%Y')emdIssueDate,date_format(PTB.emdExpiryDate,'%d-%b-%Y')emdExpiryDate,
 PM.projectId,PM.projectCode,date_format(PM.createdOn,'%d-%b-%Y')createdOn,PM.projectName,PM.projectAmount,PM.constructionArea,PM.length
 ,PT.tenderId,date_format(PT.openingDate,'%d-%b-%Y')openingDate,PT.emdAmount,date_format(PT.submissionDate,'%d-%b-%Y')submissionDate,PT.nitDocFileName
 ,PPM.letterNo,CM.clientName
