INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = PTB.tenderId
INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = PTB.projectId
LEFT JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId
LEFT JOIN M_Client_Master CM ON CM.clientId = PPM.submittedByid
 where PTB.projectId=INT_ID and PTB.deletedFlag=0;
 
 ELSEIF (P_Action='C') THEN -- get project details by projectId
  Select  PT.tenderId,
      PM.projectId, DATE_FORMAT(PM.createdOn,'%d-%b-%Y') as createdOn,PM.projectName,
      PM.projectCode,PM.constructionArea
      ,PM.length,PM.projectAmount from T_PROJECT_MASTER PM 
      INNER JOIN T_PROJECT_TENDER PT ON PM.projectId =PT.projectId
      where PM.projectId= INT_ID AND PM.deletedFlag=0;
END IF;
END ;;
