CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_TENDER_FINANCIAL_DTLS`(
IN P_Action char(10),
IN INT_ID INT
)
BEGIN
 IF(P_Action='A') THEN  -- Get Bidder's Name from Technical Bid
 SELECT 
    BM.bidderName, BM.bidderID,TT.ID
FROM   T_PROJECT_TENDER_TECHNICAL TT
INNER JOIN   T_PROJECT_TENDER_BIDDER_DTLS BM ON FIND_IN_SET(BM.bidderID, TT.vchBidderID) > 0
WHERE  TT.tenderId = INT_ID and BM.deletedFlag=0 and TT.deletedFlag=0
ORDER BY  TT.ID;

 ELSEIF (P_Action='B') THEN  -- View Financial Bid 
  SELECT TFD.financialID,TFD.ID,PT.tenderId,PM.projectId,PT.emdAmount,
  PM.projectCode,PM.projectName,
  date_format(PT.openingDate,'%d-%b-%Y')openingDate,date_format(PT.submissionDate,'%d-%b-%Y') submissionDate,
  PPM.letterNo,TB.bidderName,TFD.bidAmount
     ,TF.actionTakenBy,TF.actionTakenUserid,date_format(TF.actionTakenOn,'%d-%b-%y')actionTakenOn,TF.statusId
  ,CASE 
        WHEN TF.statusId = 1 THEN CONCAT('Pending at ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(TF.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN TF.statusId = 2 THEN CONCAT('Approved by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(TF.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN TF.statusId = 3 THEN CONCAT('Rejected by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(TF.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        WHEN TF.statusId = 4 THEN CONCAT('Pending by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(TF.actionTakenOn, '%d-%b-%Y'), 'N/A'))
         WHEN TF.statusId = 0 THEN CONCAT('Pending by ', IFNULL(UM.VCHUSERNAME, 'Unknown User'), ' (', IFNULL(DM.NVCHDESIGNAME, 'N/A'), ') on ', IFNULL(DATE_FORMAT(TF.actionTakenOn, '%d-%b-%Y'), 'N/A'))
        ELSE 'Status Not Available'
    END AS statusMsg,
        UM.VCHUSERNAME as UserName,TF.createdBy
  FROM T_PROJECT_TENDER_FINANCIAL TF 
 INNER JOIN  T_PROJECT_TENDER_FINANCIAL_DTLS TFD  ON TFD.ID=TF.ID
 INNER JOIN T_PROJECT_MASTER PM ON PM.projectId = TF.projectId
 INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = TF.tenderId
 LEFT JOIN M_Proposal_Master PPM ON  PPM.proposalId=PM.proposalId and TF.deletedFlag=0
 LEFT JOIN T_PROJECT_TENDER_BIDDER_DTLS TB ON TB.bidderID = TFD.bidderID
   LEFT JOIN  M_POR_USER UM ON UM.INTUSERID = TF.actionTakenUserid
   LEFT JOIN M_ADM_DESIGNATION DM ON DM.INTDESIGID = UM.INTDESIGNATIONID
 where TFD.winnerStatus=1 and TF.deletedFlag=0 and TFD.deletedFlag=0;
 
 ELSEIF (P_Action='C') THEN -- GET Basic Details 
  select PM.projectId,PT.tenderId,date_format(PM.createdOn,'%d-%b-%Y')createdOn,
    PM.projectName,PM.projectCode,PM.projectAmount,PM.projectAmount,PM.constructionArea,PM.length
    ,CASE WHEN TT.technicalStatus='1' THEN 'Yes'
     WHEN TT.technicalStatus='2' THEN 'No' end as  technicalStatus, TT.technicalStatus as int_technicalStatus
    from T_PROJECT_TENDER_TECHNICAL TT 
    INNER JOIN  T_PROJECT_MASTER PM ON PM.projectId =TT.projectId
    INNER JOIN T_PROJECT_TENDER PT ON PT.tenderId = TT.tenderId where TT.projectId=INT_ID and TT.deletedflag=0;
    
    ELSEIF (P_Action='D') THEN -- get Details for Edit
    SELECT 
   TFD.bidderID,PTB.bidderName,
   TFD.ID,TFD.financialScore,TFD.bidAmount,TFD.financialBidDocFile,TFD.loaDocFile,TFD.winnerStatus,
   date_format(PM.createdOn,'%d-%b-%Y')createdOn,PM.projectId,PM.projectName,PM.projectCode,PM.projectAmount,PM.projectAmount,PM.constructionArea,PM.length
    FROM   T_PROJECT_TENDER_FINANCIAL_DTLS TFD
    INNER JOIN T_PROJECT_TENDER_FINANCIAL TF ON TF.ID=TFD.ID 
    INNER JOIN  T_PROJECT_MASTER PM ON PM.projectId =TF.projectId
    INNER JOIN T_PROJECT_TENDER_BIDDER_DTLS PTB ON PTB.bidderID= TFD.bidderID where TFD.ID=INT_ID and TFD.deletedFlag=0;
    
   ELSEIF(P_Action='E') THEN -- View Financial Details
   SELECT 
   TFD.bidderID,PTB.bidderName,
   TFD.ID,TFD.financialScore,TFD.bidAmount,TFD.financialBidDocFile,TFD.loaDocFile,TFD.winnerStatus,
   date_format(PM.createdOn,'%d-%b-%Y')createdOn,PM.projectId,PM.projectName,PM.projectCode,PM.projectAmount,PM.projectAmount,PM.constructionArea,PM.length,
    PPM.letterNo,CM.clientName,
    date_format(PT.openingDate,'%d-%b-%Y')openingDate,date_format(PT.submissionDate,'%d-%b-%Y')submissionDate,PT.nitDocFileName,PT.emdAmount
   ,TF.actionTakenBy,TF.actionTakenUserid,date_format(TF.actionTakenOn,'%d-%b-%y')actionTakenOn,TF.statusId
   FROM   T_PROJECT_TENDER_FINANCIAL_DTLS TFD
    INNER JOIN T_PROJECT_TENDER_FINANCIAL TF ON TF.ID=TFD.ID 
    INNER JOIN  T_PROJECT_MASTER PM ON PM.projectId =TF.projectId
    INNER JOIN T_PROJECT_TENDER_BIDDER_DTLS PTB ON PTB.bidderID= TFD.bidderID
    INNER JOIN T_PROJECT_TENDER PT ON PT.projectId =TF.projectId
    LEFT JOIN M_Proposal_Master PPM ON PPM.proposalid = PM.proposalId
	LEFT JOIN M_Client_Master CM ON CM.clientId = PPM.submittedByid
    where TFD.ID=INT_ID and TFD.deletedFlag=0 and PTB.deletedFlag=0 and TF.deletedFlag=0 and PT.deletedFlag=0;
 END IF;
END ;;
