		scm.subCategoryName,
		pm.letterNo as proposalLetterNo,
		pm.proposalLetterDate,
		pm.subject as proposalSubject,
		pm.letterDocPath as proposalletterPath,
		ta.estimattedCost as taestimatedCost,
		aa.aaLetterPath,
		aa.aaLetterDate,
		ts.tsLetterPath,
		ts.tsLetterGendate,
        ts.boqPath,
        COALESCE(ts.generateStatus,0) as generateStatus,
        pm.stepId,
        (case when P_SearchType='NewApplication' then 2 else 3 end) as type,
		-- ts.tsLetterDate
		(
			CASE 
				WHEN (pm.proposalStatus = 3 AND pm.statusId = 3 and ts.generateStatus is null) THEN 'Pending Generate'
				WHEN (pm.proposalStatus = 3 AND pm.statusId = 3 and ts.generateStatus=1) THEN 'Pending Upload'
				WHEN (pm.proposalStatus = 3 AND pm.statusId = 3 and ts.generateStatus=2) THEN 'Pending Submission'
				ELSE 'None'
			END
		) as status
		FROM M_Proposal_Master pm 
		left join T_PROPOSAL_TA_DETAILS ta on ta.proposalid=pm.proposalid
		left join T_PROPOSAL_AA_DETAILS aa on aa.proposalid=pm.proposalid
		
		left join T_PROPOSAL_TS_DETAILS ts on ts.proposalId=pm.proposalid
		left join M_Scheme_Master sm on aa.schemeId=sm.schemeId
		JOIN M_Project_Category_Master cm ON pm.categoryid = cm.categoryid
		JOIN M_Project_Subcategory_Master scm ON pm.subCategoryid = scm.subCategoryid
		join M_Client_Master cl on pm.submittedByid=clientId
		AND (
			-- Apply filter dynamically based on P_SearchType
				CASE 
					WHEN 'NewApplication' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId IN (1,2,3))
					WHEN 'Inprogress' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId = 3)
					WHEN 'Approved' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId = 5)
					WHEN 'Cancelled' = P_SearchType THEN (pm.proposalStatus = 4 AND pm.statusId = 4)
					ELSE 1 = 1 -- No additional filter applied if P_SearchType is not specified
				END
			)
		AND (
					-- Enforce strict condition first
					(
						pm.proposalStatus = 4 
						AND pm.statusId IN (2,1) 
						AND @createdBy = pm.createdBy
					)
					-- If strict condition does not apply, execute other conditions
					OR 
					(
						pm.proposalStatus = 4 
						AND pm.statusId NOT IN (2,1) -- Ensures this runs only if (0,2) condition is false
						AND (
							-- If @createdBy is the officer, stop execution here
							@createdBy = pm.officerId AND pm.stepId = 2
							-- Otherwise, check Approval_Config condition
							OR (@createdBy <> pm.officerId AND pm.stepId > 2 AND @createdBy IN (
								SELECT usr.INTUSERID 
								FROM Approval_Config conf
								JOIN M_POR_USER usr ON usr.INTDESIGNATIONID = conf.intdesigid
								WHERE conf.intlevel = pm.stepId
								AND conf.FormName = TSWorkFlowID 
								AND conf.intDeleteflag = 0 
								AND conf.ApprovalType = 2
								AND (
									conf.intlevel <> 3 
									OR (conf.intlevel = 3 
										AND usr.zoneid = (SELECT zoneid FROM M_Adm_LevelDetails WHERE levelDetailId = pm.districtid)
										)
									)
							))
            )
        )
    )
		ORDER BY aa.createdOn DESC;
        SELECT 
			0 AS NewApplication,
			0 InProgress,
			0 AS Approved,
			0 AS Cancelled,
			0 AS TotalApplication;
        ELSEIF P_Action = 'B' THEN
			select 
				ts.`proposalId`,
				ts.`tsLetterPath`,
				ts.`tsLetterGendate`,
				ts.`tsLetterNo`,
				ts.`tsLetterDate`,
				ts.`tsApprovedBy`,
				ts.`executionMode`,
				ts.`boqPath`,
				ts.`skilledLabour`,
				ts.`unskilledLabour`,
				ts.`otherDocPath`,
				ts.`tsRemarks`,
				ta.estimattedCost as taEstimatedCost,
				aa.aaEstimatedAmount as aaEstimatedAmount,
				ta.taLetterPath,
				aa.aaLetterPath
			from  T_PROPOSAL_TA_DETAILS ta
			join T_PROPOSAL_AA_DETAILS aa on aa.proposalId=ta.proposalId
			left join T_PROPOSAL_TS_DETAILS ts on aa.proposalId=ts.proposalId
			where aa.proposalId=P_proposalid;
	END IF;
END ;;
