CREATE DEFINER=`Buidcou1`@`%` PROCEDURE `USP_V_VendorProfile`(
 IN P_Action CHAR(30),
 IN P_createdBy INT
)
BEGIN
    IF (P_Action = 'A') THEN  -- View Assign work View in list
       select VCHFULLNAME,VCHPREADDRESS,VCHOFFTEL,VCHEMAIL,VCHPANCARD,VCH_GSTNO,VCH_FAXNO,VCHMOBTEL,VCH_CONTECT_PERSON from  
 M_POR_USER PR where INTUSERID =P_createdBy;

      ELSEIF (P_Action='B') THEN
SELECT 
    PA.id,
    PA.projectDuration,
    PA.AgreementAmount AS agreementAmount, 
    DATE_FORMAT(PA.startDate, '%d-%b-%Y') AS startDate, 
    DATE_FORMAT(PA.endDate, '%d-%b-%Y') AS endDate, 
    PA.letterDocpath AS AssignDoc,
    PA.actionTakenBy,
    PA.statusId,
    PA.actionTakenOn,
    PA.actionTakenUserid,
    PM.projectId,
    PM.constructionArea, 
    PM.length, 
    PM.projectName,
    PM.projectCode,
    DATE_FORMAT(PT.openingDate, '%d-%b-%Y') AS openingDate, 
    DATE_FORMAT(PT.submissionDate, '%d-%b-%Y') AS submissionDate,
    CP1.VCHUSERNAME AS SubmittedBy,
    '40%' AS FinancialProgress,  
    '60%' AS PhysicalProgress
     ,PA.createdFor
FROM T_Project_AssignWork PA
LEFT JOIN T_PROJECT_TENDER PT ON PT.projectId = PA.projectId AND PT.deletedFlag = 0
LEFT JOIN T_PROJECT_MASTER PM ON PM.projectId = PA.projectId AND PM.deletedFlag = 0
LEFT JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId AND COALESCE(PPM.deletedFlag, 0) = 0
LEFT JOIN M_POR_USER CP1 ON CP1.INTUSERID = PPM.submittedByid
where PA.createdFor=P_createdBy;
ELSEIF (P_Action='C') THEN
SELECT 
    PA.id,
    PA.projectDuration,
    PA.AgreementAmount AS agreementAmount, 
    DATE_FORMAT(PA.startDate, '%d-%b-%Y') AS startDate, 
    DATE_FORMAT(PA.endDate, '%d-%b-%Y') AS endDate, 
    PA.letterDocpath AS AssignDoc,
    PA.actionTakenBy,
    PA.statusId,
    PA.actionTakenOn,
    PA.actionTakenUserid,
    PM.projectId,
    PM.constructionArea, 
    PM.length, 
    PM.projectName,
    PM.projectCode,
    DATE_FORMAT(PT.openingDate, '%d-%b-%Y') AS openingDate, 
    DATE_FORMAT(PT.submissionDate, '%d-%b-%Y') AS submissionDate,
    CP1.VCHUSERNAME AS SubmittedBy,
    '40%' AS FinancialProgress,  
    '60%' AS PhysicalProgress
     ,PA.createdFor
FROM T_Project_AssignWork PA
LEFT JOIN T_PROJECT_TENDER PT ON PT.projectId = PA.projectId AND PT.deletedFlag = 0
LEFT JOIN T_PROJECT_MASTER PM ON PM.projectId = PA.projectId AND PM.deletedFlag = 0
LEFT JOIN M_Proposal_Master PPM ON PPM.proposalId = PM.proposalId AND COALESCE(PPM.deletedFlag, 0) = 0
LEFT JOIN M_POR_USER CP1 ON CP1.INTUSERID = PPM.submittedByid
 where PA.createdFor=P_createdBy;
 
END IF;
END ;;
